import Search, { SearchProps } from "antd/es/input/Search";
import { AppDispatch, RootState } from "app/store";
import { InputPanelActions } from "features/InputPanel/InputPanelSlice";
import { nanoid } from "nanoid";
import "./TodoNavbar.scss";
import { useDispatch, useSelector } from "react-redux";
import { wrapperMessage } from "shared/helpers/messages/useMessage";
import { Button, Form, FormInstance } from "antd";
import { useRef } from "react";

const TodoNavbar = () => {
    const dispatch = useDispatch<AppDispatch>();
    const onSearch: SearchProps["onSearch"] = (title) => {
        if (title.length > 0) {
            let id = nanoid();
            let checked = false;
            dispatch(
                InputPanelActions.changeInputValue({ id, title, checked })
            );
            title = "";
        } else {
            wrapperMessage("Пожалуйста, введите название задачи");
        }
    };
    // function sub() {
    //     // Get the first form with the name
    //     // Usually the form name is not repeated
    //     // but duplicate names are possible in HTML
    //     // Therefore to work around the issue, enforce the correct index
    //     var frm = document.getElementsByName('frmn')[0];
    //     frm.onreset()
    //     console.log(frm);

    //     // frm.reset();  // Reset all form data
    //     // return false; // Prevent page refresh
    //  }
    return (
        <>
            <div className="text-container">
                <h1>Todo</h1>
            </div>
            <form id="frm" name="frmn" onReset={() => console.log("reset")}>
                <Search
                    size="large"
                    placeholder="Добавить задачу..."
                    onSearch={onSearch}
                    enterButton={
                       <button type="reset">but</button>
                    }
                />
            </form>
        </>
    );
};

export default TodoNavbar;
