import { CloseOutlined } from "@ant-design/icons";
import { Alert, Button, Row } from "antd";
import { AppDispatch } from "app/store";
import { InputPanelActions } from "features/InputPanel/InputPanelSlice";
import React, { useCallback } from "react";
import { useDispatch } from "react-redux";

interface ITodoItem {
    item: string;
}

const TodoItem = ({ item }: ITodoItem) => {
    const dispath = useDispatch<AppDispatch>();
    const handleDeletedItem = useCallback((item) => {
        dispath(InputPanelActions.deletedItem(item));
    }, []);
    return (
        <div>
            <Alert
                type="info"
                message={
                    <Row justify="space-between">
                        {item}
                        <Button type="text" onClick={(item) => handleDeletedItem()}>
                            <CloseOutlined />
                        </Button>
                    </Row>
                }
            />
        </div>
    );
};

export default TodoItem;
