import { createSlice } from "@reduxjs/toolkit";
import type { PayloadAction } from "@reduxjs/toolkit";

export interface TodoItem{
    id: number;
    title: string;
}
export const todoItem: TodoItem = {
    id: 0,
    title: "",
};

export interface InputPanelState {
    inputValue: string;
    todoItems: string[];
}
const initialState: InputPanelState = {
    inputValue: "",
    todoItems: todoItem,
};

export const InputPanelSlice = createSlice({
    name: "inputPanel",
    initialState,
    reducers: {
        changeInputValue(state, action: PayloadAction<string>) {
            state.inputValue = action.payload;
            state.todoItems = [...state.todoItems, action.payload];
        },
        deletedItem(state, action: PayloadAction<string>) {
            state.todoItems = state.todoItems.filter((item) => item!== action.payload);
        }
    },
});

export const { actions: InputPanelActions } = InputPanelSlice;
export const { reducer: InputPanelReducer } = InputPanelSlice;
