import { Avatar, Card, Divider, List, Skeleton } from "antd";
import { RootState } from "app/store";
import { useEffect, useState } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { useSelector } from "react-redux";

interface DataType {
    gender: string;
    name: {
        title: string;
        first: string;
        last: string;
    };
    email: string;
    picture: {
        large: string;
        medium: string;
        thumbnail: string;
    };
    nat: string;
}

const TodoList = () => {
    // const valueInput = useSelector((state: RootState) => state.inputPanel.inputValue);
    const todoItems = useSelector(
        (state: RootState) => state.inputPanel.todoItems
    );

    useEffect(() => {
        console.log(todoItems);
    }, [todoItems]);

    return (
        <div
            id="scrollableDiv"
            style={{
                height: 400,
                overflow: "auto",
                padding: "0 16px",
                border: "1px solid rgba(140, 140, 140, 0.35)",
                borderRadius: "4px",
                margin: "5px 0",
            }}
        >
            <InfiniteScroll
                dataLength={todoItems.length}
                next={() => console.log("loading")}
                hasMore={todoItems.length < 50}
                loader={<Skeleton avatar paragraph={{ rows: 1 }} active />}
                endMessage={<Divider plain>It is all, nothing more ü§ê</Divider>}
                scrollableTarget="scrollableDiv"
            >
                <List
                    dataSource={todoItems}
                    renderItem={(item) => <Card>{item}</Card>}
                />
            </InfiniteScroll>
        </div>
    );
};

export default TodoList;
