import Search, { SearchProps } from "antd/es/input/Search";
import { AppDispatch } from "app/store";
import { InputPanelActions } from "features/InputPanel/InputPanelSlice";
import { nanoid } from "nanoid";

import { useDispatch } from "react-redux";
import { useMessageApi } from "shared/helpers/messages/useMessage";

const TodoNavbar = () => {
    const dispatch = useDispatch<AppDispatch>();
    const onSearch: SearchProps["onSearch"] = (title, _e, info) => {
        if (title.length > 0) {
            let id = nanoid();
            dispatch(InputPanelActions.changeInputValue({ id, title }));
            console.log(info?.source, title);
        } else {
            useMessageApi("Пожалуйста, введите название задачи");
        }
    };

    return (
        <>
            <Search
                placeholder="Добавить задачу..."
                onSearch={onSearch}
                enterButton
            />
        </>
    );
};

export default TodoNavbar;
