import Search, { SearchProps } from "antd/es/input/Search";
import { AppDispatch, RootState } from "app/store";
import { InputPanelActions } from "features/InputPanel/InputPanelSlice";
import { nanoid } from "nanoid";
import "./TodoNavbar.scss";
import { useDispatch, useSelector } from "react-redux";
import { wrapperMessage } from "shared/helpers/messages/useMessage";
import { Button, Input, Space } from "antd";

const TodoNavbar = () => {
    const dispatch = useDispatch<AppDispatch>();

    const searchValue = String(useSelector((state: RootState) => state.inputPanel.inputValue));
    // const onSearch: SearchProps["onSearch"] = (title, _e, info) => {
    //     if (title.length > 0) {
    //         let id = nanoid();
    //         let checked = false;
    //         dispatch(
    //             InputPanelActions.changeInputValue({ id, title, checked })
    //         );
    //         console.log(info?.source, title);
    //     } else {
    //         wrapperMessage("Пожалуйста, введите название задачи");
    //     }
    // };
    let id = nanoid();
    let checked = false;
    const onSearch = (title: string) => {
        if (title.length > 0) {
            // let id = nanoid();
            // let checked = false;
            dispatch(
                InputPanelActions.changeInputValue({ id, title, checked })
            );
        } else {
            wrapperMessage("Пожалуйста, введите название задачи");
        }

    };
    const handlerAddItem = (searchValue: string) => {
            dispatch(InputPanelActions.addItem({ id, searchValue, checked }));
        }

    return (
        <>
            <div className="text-container">
                <h1>Todo</h1>
            </div>
            <Space.Compact style={{ width: "100%" }}>
                <Input
                    placeholder="Добавить задачу..."
                    size="large"
                    onChange={(e) => onSearch(e.target.value)}
                />
                <Button type="primary" size="large" onClick={() => handlerAddItem(searchValue)}>
                    Добавить
                </Button>
            </Space.Compact>
            <Search
                size="large"
                placeholder="Добавить задачу..."
                // onSearch={onSearch}
                enterButton
            />
        </>
    );
};

export default TodoNavbar;
